import React, { useContext, useState, useRef, useEffect, useMemo } from "react";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Paper from "@material-ui/core/Paper";
import CssBaseline from "@material-ui/core/CssBaseline";

import Alert from "@material-ui/lab/Alert";
import Snackbar from '@material-ui/core/Snackbar';
import CloseIcon from '@material-ui/icons/Close';

import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Switch from "@material-ui/core/Switch";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import "date-fns";
import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker
} from "@material-ui/pickers";

import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import CloudDoneIcon from "@material-ui/icons/CloudDone";
import InputAdornment from "@material-ui/core/InputAdornment";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import AccountCircle from "@material-ui/icons/AccountCircle";
import MenuItem from "@material-ui/core/MenuItem";

import clsx from "clsx";
import { useMutation } from "@apollo/react-hooks";
import gql from "graphql-tag";
import { useHistory } from "react-router-dom";

import { findEmpty } from "../Services/functions";
import { UserContext } from "../userContext";

import Copyright from "../Atoms/copyright";
import Dropzone from "../Molecules/Dropzone";
import Spinner from "../Atoms/Spinner";
import MapCreate from "../Molecules/MapCreate";
import LoginFirstButton from "../Atoms/LoginFirstButton";

import {useLogicPlusMinusValue} from "./Logic/Create/useLogicPlusMinusValue";


const NEW_EVENT = gql`
  mutation createEvent(
    $name: String!
    $lng: Float
    $lat: Float
    $address: String
    $author: String!
    $dateStart: String
    $duration: Float
    $price: Float
    $currency: String
    $capacityMax: Int
    $BYO: Boolean
    $description: String
    $imagesArr: [ImageInput]
  ) {
    createEvent(
      eventInput: {
        name: $name
        lng: $lng
        lat: $lat
        address: $address
        author: $author
        dateStart: $dateStart
        duration: $duration
        price: $price
        currency: $currency
        capacityMax: $capacityMax
        BYO: $BYO
        description: $description
        imagesArr: $imagesArr
      }
    ) {
      dataOut {
        _id
        name
        success
      }
      errorOut {
        name
        message
      }
    }
  }
`;

const currencies = [
  {
    value: "USD",
    label: "$"
  },
  {
    value: "EUR",
    label: "€"
  },
  {
    value: "CZK",
    label: "Kč"
  }
];

function Create(props) {
  const classes = useStyles();
  let history = useHistory();

  const { context } = useContext(UserContext);
  const [customMapParam, setCustomMapParam] = useState();
  const Price = useLogicPlusMinusValue("plus_btn", "minus_btn", 50)
  const Capacity = useLogicPlusMinusValue("plus_btn_capacity", "minus_btn_capacity", 15)
  const Duration = useLogicPlusMinusValue("plus_btn_duration", "minus_btn_duration", 3)
  const [formValue, setFormValue] = useState({
    startDate: new Date(),
    currency: "CZK",
    BYO: true
  });

  const price = Price.value
  const plusClickPrice = Price.plusClickValue
  const minusClickPrice = Price.minusClickValue
  const setNewValuePrice = Price.setNewValue


  const capacity = Capacity.value
  const plusClickCapacity = Capacity.plusClickValue
  const minusClickCapacity = Capacity.minusClickValue
  const setNewValueCapacity = Capacity.setNewValue

  const duration = Duration.value
  const plusClickDuration = Duration.plusClickValue
  const minusClickDuration = Duration.minusClickValue
  const setNewValueDuration = Duration.setNewValue


  const [FeErrors, setFeErrors] = useState([]);
  const [createEvent, { loading, error, data }] = useMutation(NEW_EVENT, {
    onCompleted: () => {
      setFeErrors([]);
    }
  });

  const { dataOut } = data ? data.createEvent : { dataOut: undefined };
  const { errorOut } = data ? data.createEvent : { errorOut: undefined };

  let den = new Date(formValue.startDate);




  //Day +- one day
  const plusDay = () => {
    den.setDate(den.getDate() + 1);
    setFormValue(prev => {
      return { ...prev, startDate: den };
    });
  };

  const minusDay = () => {
    den.setDate(den.getDate() - 1);
    setFormValue(prev => {
      return { ...prev, startDate: den };
    });
  };

  //Hours +- one hour
  const plusHour = () => {
    den.setHours(den.getHours() + 1);
    let isoDen = den.toISOString().split(":")[0];
    setFormValue(prev => {
      return { ...prev, startDate: `${isoDen}:00:00.000Z` };
    });
  };

  const minusHour = () => {
    den.setHours(den.getHours() - 1);
    let isoDen = den.toISOString().split(":")[0];
    setFormValue(prev => {
      return { ...prev, startDate: `${isoDen}:00:00.000Z` };
    });
  };


  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  useEffect(() => {
    if (dataOut) {
      setTimeout(() => {
        history.push(`/event/${dataOut._id}`);
      }, 500);
    }
  }, [dataOut]);

  const inputName = useRef(null);
  const inputTime = useRef(null);
  const inputBYO = useRef(null);
  const inputDescription = useRef(null);
  const inputMarker = useRef(null);

  const onSubmit = e => {
    e.preventDefault();

    let load = {
      name: inputName.current.value ? inputName.current.value : null,
      lng: customMapParam.lng,
      lat: customMapParam.lat,
      address: customMapParam.address,
      author: context._id,
      eventType: 1,
      dateStart: formValue.startDate,
      duration: duration,
      price: price,
      currency: formValue.currency,
      capacityMax: capacity,
      BYO: inputBYO.current.checked,
      description: inputDescription.current.value
        ? inputDescription.current.value
        : null,
      imagesArr: formValue.ImagesArr
    };

    const empty = findEmpty(load);


    if (empty.length == 0) {
      createEvent({
        variables: load
      });
    } else {
      setFeErrors(empty);
    }
  };

  const handleDateChange = date => {
    setFormValue(prev => {
      return { ...prev, startDate: date };
    });
  };

  const handleChangeCurrency = event => {
    setFormValue(prev => {
      return { ...prev, currency: event.target.value };
    });
  };

  const handleChangeBYO = () => {
    setFormValue(prev => {
      return { ...prev, BYO: !formValue.BYO };
    });
  }


  return (
    <div
      component="main"
      id="mainCreate"
      className={classes.profileWrap}
      style={{ position: context.freezScroll ? "fixed" : "absolute" }} // fixed is freezed, absolute is scrollable
    >
      <CssBaseline />
      <Container maxWidth="sm" className={classes.paper1}>
        <Grid
          container
          justify="center"
          alignItems="center"
          direction="column"
          className={classes.pinkBack}
        >

          {/* <Avatar className={classes.avatar}>
            <AddCircleOutlineIcon />
          </Avatar> */}
          <Grid item>
            <Typography variant="h5" className={classes.justDoIt}>
              JUST DO IT
            </Typography>
          </Grid>
          <Grid item>
            <Typography variant="h5" className={classes.now}>
              <b>NOW</b>
            </Typography>
          </Grid>

          <Grid container className={clsx(classes.formRow)}>
            <Grid item style={{ width: "100%" }}>
              {context.success ? (
                <TextField
                  variant="outlined"
                  margin="normal"
                  required
                  fullWidth
                  id="name"
                  inputRef={inputName}
                  label="My First Party"
                  name="name"
                  autoComplete="name"
                  //autoFocus
                  style={{
                    background: "rgba(255,255,255,0.9)",
                    borderRadius: 5,
                    boxShadow: "5px 5px 5px 0px rgba(0,0,0,0.75)"
                  }}
                />
              ) : (
                <LoginFirstButton />
              )}
            </Grid>
          </Grid>
        </Grid>
      </Container>
      <Container maxWidth="sm" className={classes.paper}>
        <form className={classes.form} noValidate>
          <InputLabel htmlFor="standard-adornment-amount">ADDRESS</InputLabel>
          <MapCreate
            customMapParam={customMapParam}
            setCustomMapParam={setCustomMapParam}
            inputMarker={inputMarker}
          />

          <InputLabel htmlFor="standard-adornment-amount">DATE</InputLabel>
          <Grid
            container
            alignItems="center"
            alignContent="flex-start"
            justify="flex-start"
            className={clsx(classes.settingsPanel, classes.formRow)}
          >
            <Grid item>
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                onClick={() => {
                  minusDay();
                }}
              >
                <ArrowBackIosIcon color="primary" />
              </IconButton>
            </Grid>
            <Grid item>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  margin="normal"
                  id="date-picker-dialog"
                  format="dd/MM/yyyy"
                  value={formValue.startDate}
                  onChange={e => {
                    handleDateChange(e);
                  }}
                  KeyboardButtonProps={{
                    "aria-label": "change date"
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>
            <Grid item>
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                onClick={() => {
                  plusDay();
                }}
              >
                <ArrowForwardIosIcon
                  //fontSize="large"
                  color="primary"
                />
              </IconButton>
            </Grid>
          </Grid>

          {/*  +- Hours */}

          <InputLabel htmlFor="standard-adornment-amount">TIME</InputLabel>
          <Grid
            container
            alignItems="center"
            alignContent="flex-start"
            justify="flex-start"
            className={clsx(classes.settingsPanel, classes.formRow)}
          >
            <Grid item>
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                onClick={() => {
                  minusHour();
                }}
              >
                <ArrowBackIosIcon
                  //fontSize="large"
                  color="primary"
                />
              </IconButton>
            </Grid>
            <Grid item>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardTimePicker margin="normal" id="time-picker" inputRef={inputTime}
                  //label="Time picker"
                  value={formValue.startDate}
                  onChange={e => {
                    handleDateChange(e);
                  }}
                  KeyboardButtonProps={{
                    "aria-label": "change time"
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>
            <Grid item>
              <IconButton color="inherit" aria-label="open drawer" edge="start"
                onClick={() => {
                  plusHour();
                }}
              >
                <ArrowForwardIosIcon
                  //fontSize="large"
                  color="primary"
                />
              </IconButton>
            </Grid>
          </Grid>

          <InputLabel htmlFor="standard-adornment-amount">DURATION</InputLabel>
          <Grid
            container
            className={clsx(classes.settingsPanel, classes.formRow)}
          >
            <Grid item>
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                id="minus_btn_duration"
                onClick={() => minusClickDuration}
              >
                <ArrowBackIosIcon color="primary" />
              </IconButton>
            </Grid>
            <Grid item>
              <Input
                type="number"
                value={duration}
                //onChange={handleChange("amount")}
                onChange={(value) => {setNewValueDuration(value.target.value)}}
                endAdornment={
                  <InputAdornment position="start">
                    hours  
                  </InputAdornment>
                }
              />
            </Grid>
            <Grid item>
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                id="plus_btn_duration"
                onClick={() => plusClickDuration}
              >
                <ArrowForwardIosIcon color="primary" />
              </IconButton>
            </Grid>
          </Grid>


          <InputLabel htmlFor="standard-adornment-amount">PRICE</InputLabel>
          <Grid
            container
            className={clsx(classes.settingsPanel, classes.formRow)}
          >
            <Grid item>
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                id="minus_btn"
                onClick={() => minusClickPrice}
              >
                <ArrowBackIosIcon color="primary" />
              </IconButton>
            </Grid>
            <Grid item>
              <Input
                type="number"
                value={price}
                //onChange={handleChange("amount")}
                onChange={(value) => {
                  console.log("CHange Price: ", value)
                  setNewValuePrice(value.target.value)
                }}
                startAdornment={
                  <InputAdornment position="start">
                    {formValue.currency}
                  </InputAdornment>
                }
              />
            </Grid>
            <Grid item>
              <TextField
                id="outlined-select-currency"
                select
                //label="Select"
                value={formValue.currency}
                onChange={handleChangeCurrency}
                //helperText="Please select your currency"
                //variant="outlined"
              >
                {currencies.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>

            <Grid item>
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                id="plus_btn"
                onClick={() => plusClickPrice}
              >
                <ArrowForwardIosIcon color="primary" />
              </IconButton>
            </Grid>
          </Grid>

          <InputLabel htmlFor="standard-adornment-amount">CAPACITY</InputLabel>
          <Grid container className={classes.formRow}>
            <Grid item>
              <IconButton
                color="inherit"
                edge="start"
                id="minus_btn_capacity"
                onClick={() => minusClickCapacity}
              >
                <ArrowBackIosIcon color="primary" />
              </IconButton>
            </Grid>
            <Grid>
              <TextField
                value={capacity}
                type="number"
                onChange={(value) => {
                  setNewValueCapacity(value.target.value)
                }}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <AccountCircle />
                    </InputAdornment>
                  )
                }}
              />
            </Grid>
            <Grid item>
              <IconButton
                color="inherit"
                aria-label="open drawer"
                edge="start"
                id="plus_btn_capacity"
                onClick={() => plusClickCapacity}
              >
                <ArrowForwardIosIcon color="primary" />
              </IconButton>
            </Grid>
          </Grid>

          <InputLabel htmlFor="standard-adornment-amount">BYO Event (guest can bring his own dring/snack)</InputLabel>
          <FormControlLabel
            className={classes.switchBYO}
            control={
              <Switch
                //checked={formValue.checkedA}
                onChange={handleChangeBYO}
                checked={formValue.BYO}
                inputRef={inputBYO}
              />
            }
          />
          <InputLabel htmlFor="standard-adornment-amount">
            DESCRIPTION
          </InputLabel>
          <Grid
            container
            className={clsx(classes.formRow, classes.descriptionContainer)}
          >
            <Grid item className={classes.descriptionItem}>
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                id="decsription"
                placeholder="Example: Upon arrival, you will get welcome drink and some snacks will be ready on the balcony. Grill be ready too."
                multiline
                rows="4"
                inputRef={inputDescription}
                //label="Description"
                name="decsription"
                autoComplete="false" //improvisation, should be "off", or random "string"
              />
            </Grid>
          </Grid>
          <InputLabel htmlFor="standard-adornment-amount">IMAGES</InputLabel>
          <Grid
            container
            justify="center"
            alignContent="center"
            className={classes.formRow}
          >
            <Grid
              item
              className={clsx(classes.dropItem, classes.dropContainer)}
            >
              <Dropzone setFormValue={setFormValue} />
            </Grid>
          </Grid>

          {errorOut &&
            errorOut.map(item => (
              <Alert severity="error" key={item.message}>
                {item.message}
              </Alert>
            ))}

          {FeErrors &&
            FeErrors.map(item => (
              <Alert severity="error" key={item}>
                {item} is empty
              </Alert>
            ))}

          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={e => onSubmit(e)}
            // disabled={
            //   formValue.ImagesArr && formValue.ImagesArr.length ? false : true
            // }
            disabled={loading}
          >
            {dataOut && <CloudDoneIcon />}
            {loading && <Spinner hieght={20} width={20} />}
            Create Party
          </Button>
        </form>
      </Container>
      <Box mt={8}>
        <Copyright />
      </Box>
    </div>
  );
}

