import React, {useContext} from "react";
import Grid from "@material-ui/core/Grid";
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import FilterListIcon from '@material-ui/icons/FilterList';
import { makeStyles } from "@material-ui/core/styles";
import { Animated } from "react-animated-css";
import { UserContext } from "src/userContext";

import { useHistory } from "react-router-dom";

function NoEventsBoard(props) {
  let history = useHistory();
  const classes = useStyles();
  const { context, setContext } = useContext(UserContext);

  const handleFilterOnOff = (e) => {
    e.stopPropagation();
    console.log("Filter ON,Off: ", e.target.checked )
    setContext(prev => {return {
      ...prev, 
      playFilterObj: {
        ...prev.playFilterObj,
        filterOn: !context.playFilterObj.filterOn ,
        shownEvents: []
      }
      }});
  };


  return (
    <Grid
      container
      className={classes.mainGrid}
      onClick={() => {
        setTimeout(() => {
          history.push(`/`);
        }, 200);
      }}
    >
      <Grid item xs={12} className={classes.loginTransparent}>
        <Grid
          container
          justify="center"
          alignItems="center"
        >
          <Grid item>
            <p className={classes.loginFirstContainer}>
              There are no more events <b>within {context.radius} km</b> from <b>{context.curPositionAddress}</b> in <b>{context.plusDays}</b> days from today
            </p>
          </Grid>

        <Grid item className={classes.turnOffItem}>
        <FormControlLabel 
          control={<Switch  
                      checked={context.playFilterObj.filterOn} 
                      onChange={handleFilterOnOff}
                      onClick={(e) => {e.stopPropagation()}}
                      value="checkedA"
                      inputProps={{ 'aria-label': 'secondary checkbox' }}
                        />} 
          label={`filter ${context.playFilterObj.filterOn ? "ON" : "OFF"}`} />
        </Grid> 
        </Grid>
      </Grid>
      {/* <Grid item xs={12} className={classes.fingerItem}>
        <Grid container direction="column" justify="center" alignItems="center" style={{height: '100%'}}>
          <Grid item>
              <FilterListIcon color='secondary' className={classes.arrowIcon} />
          </Grid>
        </Grid>
      </Grid> */}
      <Grid item xs={12} className={classes.loginBlack}>
        <Grid
          container
          direction="row"
          justify="center"
          alignItems="center"
          className={classes.loginContainer}
        >
          <Grid item>
            <Typography variant="h5" className={classes.backToApp}>
              <b>Back to Main Page</b>
            </Typography>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
}

const useStyles = makeStyles(theme => ({
  mainGrid: {
    width: 250,
    backgroundColor: '#CECDCD', //theme.palette.charliePink,
    borderRadius: 20,
    color: "black",
    boxShadow: "4px 3px 5px 0px rgba(0,0,0,0.5)"
  },

  loginTransparent: {
    height: 180
  },
  loginFirstContainer: {
    // height: "100%",
    fontSize: 16,
    fontWeight: 400,
    textAlign: 'center',
    padding: 5
  },
  // fingerItem: {
  //   height: 60
  // },
  backToApp:{
    padding: 10,
    textAlign: "center",
    width: "100%"
  },
  arrowIcon: {
    height: 50,
    width: 50,
    color: 'grey'
  },

  loginBlack: {
    backgroundColor: theme.palette.charliePink,
    color: "white",
    height: "25%",
    borderBottomLeftRadius: 20,
    borderBottomRightRadius: 20
  },
  loginContainer: {
    height: "100%"
  }
}));

export default NoEventsBoard;
