import React, { useState, useReducer, useEffect, useContext } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Slider from '@material-ui/core/Slider';
import Collapse from '@material-ui/core/Collapse';
import Typography from '@material-ui/core/Typography';
import Chip from '@material-ui/core/Chip';
import Backdrop from '@material-ui/core/Backdrop';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';

import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';
import AllOutIcon from '@material-ui/icons/AllOut';
import DateRangeIcon from '@material-ui/icons/DateRange';
import CloseIcon from "@material-ui/icons/Close";
import CircularProgress from '@material-ui/core/CircularProgress';

import clsx from "clsx";
import { withRouter, useHistory } from "react-router-dom";

import { UserContext } from "../../userContext";


const SettingsPanel = ({loading}) => {
  const classes = useStyles();
  let history = useHistory();
  const { context, setContext } = useContext(UserContext);
  const [workingValue, setWorkingValue] = useState({radius: context.radius, days: context.plusDays});
  const [close, setClose] = useState(false);
  const [checked, setChecked] = useState(false);

  useEffect(() => {
    console.log("Setitngs panel context: ", context)
  })

  const handleChange = (e) => {
    e.stopPropagation();
    setChecked(prev => !prev);
  };

  const handleFilterOn = (e) => {
    e.stopPropagation();
    setContext(prev => {return {...prev, filterOn: !context.filterOn ,shownEvents: []}});
  };

  const handleChangeRadius = (e, newValue) => {
    e.preventDefault()
    setContext(prev => {return {...prev,shownEvents: [], playEventsCount: null, radius: newValue}});
    // setPlayFilter(prev => {return {...prev,radius: newValue}});
  };

  const handleChangeDays = (e, newValue) => {
    e.preventDefault()
    setContext(prev => {return {...prev,shownEvents: [], playEventsCount: null, days: newValue}});
    // setPlayFilter(prev => {return {...prev,days: newValue}});
  };

  const handleChangeWorkingValue = (e, target, newValue) => {
    e.preventDefault()
    setWorkingValue(prev => {return {...prev, [target]: newValue}});
  };

  const closeBackdrop = () => {
    setChecked(false)
  };

  const setCityLocation = (value) => {
    setContext(prev => {
        return { ...prev, geolocationObj: {lng: 14.42076 , lat: 50.08804}};
      });
  }

  return (
    <div className={classes.eggContainerWrap}>

      <Backdrop className={classes.backdrop} 
                open={checked} 
                onClick={closeBackdrop} 
                className={classes.backdropMain}>
        <FiberManualRecordIcon color="inherit" style={{height: 60, width: 60}} />
      </Backdrop>

      <Container
          maxWidth="xs"
          className={classes.playContainer}
          style={{padding: 0}}
          onClick={(e) => {e.preventDefault()}}
        >
      <Grid container className={clsx(classes.eggContainerTop, checked && classes.solidColor)}>
        <Grid item xs={10}>
            <Grid container onClick={handleChange}>

                <Grid item xs={5} >
                  <Grid container justify="center">
                    <Grid item xs={12}>
                      <Chip icon={<AllOutIcon className={classes.allOutIcon} style={{color: !context.filterOn ? '#D1D0D0' : "#E8045D"}}/>} 
                            label={`+ ${context.radius} km`} 
                            variant="outlined" 
                            // color="secondary" 
                            style={{color: !context.filterOn ? '#D1D0D0' : "#E8045D"}}
                            onClick={() => console.log("Yess")}
                            className={clsx(classes.anyChip, !context.filterOn && classes.lightGrey)} />
                    </Grid>
                  </Grid>
                </Grid>
                <Grid item xs={5}>
                  <Grid container justify="center">
                    <Grid item xs={12}>
                        <Chip icon={<DateRangeIcon style={{height: 20, width: 20, color: !context.filterOn ? '#D1D0D0' : "#59F0EA"}} />} // '#D1D0D0'
                              label={`+ ${context.plusDays} days`} 
                              variant="outlined" 

                              className={clsx(classes.anyChip, classes.lightBlue, !context.filterOn && classes.lightGrey)} />
                    </Grid>
                  </Grid>
                </Grid>            
                <Grid item xs={2}>
                  <Grid container justify="center">
                    <Grid item xs={12}>
                        <Chip label={!loading ? `${context.shownEvents.length + 1}/${context.playEventsCount}` : null} 
                              icon={loading && <CircularProgress style={{height: 20, width: 20, color: 'white', left: 6, position: "relative"}} />}
                              variant="default" 
                              color="secondary"
                              className={classes.anyChip} />
                    </Grid>
                  </Grid>
                </Grid>
                <Grid item xs={12}>
                {context.curPositionAddress}
                </Grid>
                <Grid item  xs={12}>

                <Collapse in={checked}>
                  <Grid container alignItems="center" className={classes.collapseGrid}>
                      <Grid item xs={2}>
                          <Typography id="range-slider" color="secondary" gutterBottom className={clsx(classes.textSize, !context.filterOn && classes.lightGrey)}>
                              0 km
                          </Typography>
                      </Grid>
                      <Grid item xs={8}>

                          <Slider
                              // defaultValue={playFilter.radius}
                              value={workingValue.radius}
                              onChange={(e, value) => handleChangeWorkingValue(e, "radius", value)}
                              step={5}
                              min={5}
                              max={50}
                              disabled={!context.filterOn}
                              onChangeCommitted={(e_, value) => {
                                handleChangeRadius(e_ , value)
                              } 
                            }
                            style={{color: context.filterOn ? '#E8045D' : '#D1D0D0'}}
                              // valueLabelDisplay="auto"
                              // aria-labelledby="range-slider"
                              // getAriaValueText={valuetext}
                          /> 
                      </Grid>
                      <Grid item xs={2}>
                          <Typography id="range-slider" color="secondary" gutterBottom className={clsx(classes.textSize, !context.filterOn && classes.lightGrey)}>
                              {50} km
                          </Typography>
                      </Grid>

                      <Grid item xs={2} className={classes.lightBlue}>
                          <Typography id="range-slider" gutterBottom className={classes.textSize, !context.filterOn && classes.lightGrey}>
                              Today
                          </Typography>
                      </Grid>
                      <Grid item xs={8}>

                            <Slider valueLabelDisplay="auto" 
                                  aria-label="none" 
                                  value={workingValue.days} 
                                  onChange={(e, value) => handleChangeWorkingValue(e, "days", value)}
                                  step={1}
                                  min={0}
                                  max={7}
                                  disabled={!context.filterOn}
                                  onChangeCommitted={(e_, value) => {
                                    handleChangeDays(e_ , value)
                                  } 
                                }
                                  style={{color: context.filterOn ? "#59F0EA" : '#D1D0D0'}}
                            /> 
                      </Grid>
                      <Grid item xs={2} className={classes.lightBlue}>
                          <Typography id="range-slider" gutterBottom className={classes.textSize, !context.filterOn && classes.lightGrey}>
                            + {context.plusDays} days
                          </Typography>
                      </Grid>
                      <Grid item xs={12}>
                      <Select
                          labelId="demo-controlled-open-select-label"
                          id="demo-controlled-open-select"
                          // open={open}
                          // onClose={handleClose}
                          // onOpen={handleOpen}
                          // value={age}
                          onChange={setCityLocation}
                        >
                          <MenuItem value="">
                            <em>None</em>
                          </MenuItem>
                          <MenuItem value={10}>Prague</MenuItem>
                          <MenuItem value={20}>Sydney</MenuItem>
                          <MenuItem value={30}>Gold Coast</MenuItem>
                        </Select>
                      </Grid>


                  </Grid>
                </Collapse>  
                <Grid item xs={12} className={classes.turnOffItem}>
                      <FormControlLabel control={<Switch  checked={context.filterOn} 
                                              onChange={handleFilterOn}
                                              onClick={(e) => {e.stopPropagation()}}
                                              value="checkedA"
                                              inputProps={{ 'aria-label': 'secondary checkbox' }}
                                      />} 
                            label={`filter ${context.filterOn ? "ON" : "OFF"}`} />
                      </Grid>   

                </Grid>
                {/* <Grid item xs={2} className={classes.arrowGrid} style={{display: "none"}} >
                    <KeyboardArrowDownIcon 
                            color="secondary" 
                            fontSize="large" 
                            className={clsx(classes.arrowOpener, checked && classes.arrowUp)} 
                            onClick={handleChange} />
                </Grid> */}
            </Grid>

            </Grid>
        <Grid item 
                xs={2} 
                id="play_close"
                className={classes.closeCrossGrid}
                >
          <Grid container justify="center">
              <Grid item>
                <CloseIcon 
                    className={clsx(classes.closeCross, close && classes.whiteBordered)} 
                    onClick={() => {
                    setClose(true)
                    setContext(prev => {return {...prev, shownEvents: []}});
                    setTimeout(() => {
                      history.goBack()
                    }, 100)
                    }}
                    />
              </Grid>
          </Grid>
        </Grid>
      </Grid>
      </Container>
    </div>
  );
};


